        console.log\(''lib download:'', lib.path\);
        return url;
      },
      assetsHost: BMCLAPI,
    }\);
    console.log\(''DONE''\);
  } catch\(e\) {
    console.error\(''ERR:'', e.message\);
    if \(e.errors\) {
      for \(const sub of e.errors\) {
        console.error\(''  sub:'', sub.message, sub.url || ''''\);
      }
    }
  }
}
test\(\);
")

